import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function RadiobuttonForm() {

  const [option, setOption] = useState("Red");

  //Obsługa wyboru
  const handleChange = (event) => {
    alert('Wybrano kolor: ' + option);    
    event.preventDefault();    
  }

  //Obsługa przycisku
  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano kolor: ' + option);
  }

  //Obsluga aktualizacji

    return (

        <Container>

            <h3>Aplikacja demonstrująca pole wyboru "jeden z wielu" (HOOK)</h3>

            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Wybierz swój ulubiony kolor:</Form.Label>
                    <Form.Check
                        type={"radio"}
                        label="Red"
                        value={"Red"}
                        onChange={(e) => setOption(e.target.value)}
                        checked={option === "Red"}>
                    </Form.Check>
                    <Form.Check
                        type={"radio"}
                        label="Green"
                        value={"Green"}
                        onChange={(e) => setOption(e.target.value)}
                        checked={option === "Green"}>
                    </Form.Check>
                    <Form.Check
                        type={"radio"}
                        label="Blue"
                        value={"Blue"}
                        onChange={(e) => setOption(e.target.value)}
                        checked={option === "Blue"}>
                    </Form.Check>
                </Form.Group>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form>
        </Container>
    );

}
