import React, {useState } from 'react'
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

import SPerson from "./SPerson"


export default function ReadPersonHook() {

    const [email, setEmail] = useState("pola@gmail.com");
    const [firstName, setFirstName] = useState("Pola");
    const [lastName, setLastName] = useState("Polańska");
    const [age, setAge] = useState("22");
    

    //Obsługa przycisku
    const handleSubmit = (event) => {

        let locPerson = new SPerson(email, firstName, lastName, age);
        //props.callback(locPerson);
        event.preventDefault();
    }


    return (
        <Container>

            <Alert variant="info">
                <strong>Podaj nową osobę do bazy danych:</strong>
            </Alert>

            <Form onSubmit={handleSubmit}>
                <Form.Group>
                    <Form.Label> Email: <input type="text" name='email' value={email} onChange={(e) => setEmail(e.target.value)} /> </Form.Label>
                    {' '}
                    <Form.Label> Imię: <input type="text" name='firstName' value={firstName} onChange={(e) => setFirstName(e.target.value)} /> </Form.Label>
                    {' '}
                    <Form.Label> Nazwisko: <input type="text" name='lastName' value={lastName} onChange={(e) => setLastName(e.target.value)} /> </Form.Label>
                    {' '}
                    <Form.Label> Wiek: <input type="text" name='email' value={age} onChange={(e) => setAge(e.target.value)} /> </Form.Label>
                    {' '}
                    <Button variant="primary" type="submit">  Dodaj  </Button>
                </Form.Group>
                <p></p>
            </Form>
        </Container>
    );

}
