import React, { Component, FormEvent, ChangeEvent } from "react";

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';


//Demonstracja listy (CLASS)

class ListForm extends Component
{
  constructor(props) {
    super(props);
    this.state = { value: "kokosowy" };
    this.handleChange = this.handleChange.bind(this);    
  }

  handleChange(event) {
    alert('Wybrano smak: ' + event.currentTarget.value);
    this.setState({ value: event.currentTarget.value });
    event.preventDefault();
  }


  render() {
    return (
      <Container>
        <h3>Aplikacja demonstrująca pole wyboru z listy (CLASS)</h3>

        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Wybierz swój ulubiony smak:</Form.Label>
            <Form.Select value={this.state.value} onChange={this.handleChange}>
              <option value="grejpfrutowy">Grejpfrutowy</option>
              <option value="limonkowy">Limonkowy</option>
              <option value="kokosowy">Kokosowy</option>
              <option value="mango">Mango</option>
            </Form.Select>
          </Form.Group>
        </Form >
      </Container>
    );
  }
}

export default ListForm;
