import  SPerson from "./SPerson"

class SPersonList{

    public personList : SPerson[];
    
    constructor(){
        this.personList = [];        
    }

    public add(person : SPerson) : void {
        this.personList.push(person);
    }

    public addPerson(email : string, firstname: string, lastname : string, age : number) : void {
        let locPerson = new SPerson(email, firstname, lastname, age);
        this.personList.push(locPerson);
    }

   
    public get(index : number) : SPerson {
        return this.personList[index];
    }

    public size() : number {
        return this.personList.length;
    }


    //Utworzenie bazy poczatkowej osób
    public build() : void {
        let p1 = new SPerson("jkowalski@gmail.com","Jan","Kowalski",35);
        let p2 = new SPerson("mnowak@gmail.com","Marek","Nowak",46);
        let p3 = new SPerson("awazna@gmail.com","Anna","Wazna",26);      

        this.add(p1);this.add(p2);this.add(p3);
    }

    public deletePerson(email: string): boolean {

        if (this.size() > 0) {

            let locPersonList: SPerson[] = [];
            let finded: boolean = false;

            for (let i: number = 0; i < this.size(); i = i + 1) {
                let person = this.get(i);
                let locEmail = person.email;

                if (locEmail != email) {
                    locPersonList.push(person);
                }
                else {
                    finded = true;
                }
            }

            if (!finded) {
                return false;
            }
            else {
                this.personList = locPersonList;
                return true;                
            }
        }
        else{
            return false;            
        }

    }

    

}

export default SPersonList;
