import { Component, FormEvent, ChangeEvent } from "react";

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
//import 'bootstrap/dist/css/bootstrap.min.css';


import StringSet from "./StringSet"

interface Props {
};

interface State {
    selectedOptions: StringSet;
}

class CheckboxForm extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);

        this.state = {
            selectedOptions: new StringSet()
        };

        this.state.selectedOptions.add("Autobus");
        this.state.selectedOptions.add("Samochod");

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e: ChangeEvent<HTMLInputElement>): void {
        //alert('Wybrano: ' + e.target.value);

        if (this.state.selectedOptions.member(e.target.value)) {
            this.state.selectedOptions.delete(e.target.value);
        }
        else {
            this.state.selectedOptions.add(e.target.value);
        }

        this.setState({
            selectedOptions: this.state.selectedOptions
          });
    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {
        alert('Wybrano: ' + this.state.selectedOptions.getToString());
        event.preventDefault();
    }


    render() {
        return (

          <Container>        
        
        <h3>Aplikacja demonstrująca pole wyboru "jeden z wielu"</h3>
            <Form onSubmit={this.handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Jakimi środkami transportu podróżujesz na codzień:</Form.Label>
                    <Form.Check
                        type={"checkbox"}
                        label="Autobus"
                        value={"Autobus"}
                        checked={this.state.selectedOptions.member("Autobus")}
                        onChange={this.handleChange}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Pociag"
                        value={"Pociag"}
                        checked={this.state.selectedOptions.member("Pociag")}
                        onChange={this.handleChange}>                                                    
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Samochod"
                        value={"Samochod"}
                        checked={this.state.selectedOptions.member("Samochod")}
                        onChange={this.handleChange}>                       
                    </Form.Check>
                </Form.Group>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form>
            </Container>
        );
    }


    /*Tak bez react-bootstrap
  render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <div className="radio">
            <label>
              <input type="radio" value="Red" checked={this.state.selectedOption === "Red"} onChange={this.handleChange} />  Red
            </label>
          </div>
  
          <div className="radio">
            <label>
              <input type="radio" value="Green" checked={this.state.selectedOption === "Green"} onChange={this.handleChange} />  Blue
            </label>
          </div>
  
          <div className="radio">
            <label>
              <input type="radio" value="Blue" checked={this.state.selectedOption === "Blue"} onChange={this.handleChange} />  Blue
            </label>
          </div>
  
          <div>
            Selected option is : {this.state.selectedOption}
          </div>
  
          <button type="submit">  Submit </button>
  
        </form>
      );
    }
    */
}

export default CheckboxForm;
