import React, { useState } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table'; 
import 'bootstrap/dist/css/bootstrap.min.css';

import Footer from "./footer";
import Header from "./header";
import BookDescriptionHook from "./BookDescriptionHook";


import SBookListHook from "./SBookListHook"

//Aplikacja wykorzystująca dwa główne komponenty zalezne od siebie: BookAppHook i BookDescriptionHook
//Pokazuje filtrowanie książek w aplikacji typu SPA
//Korzysta także z 2 kompotentów pomocniczych:  Header i Footer


export default function BookAppHook(props) {


    let bList = new SBookListHook();
    bList.build(); //Wstepne wypelnianie
    let sbList = new SBookListHook();
    sbList.build(); //Wstepne wypelnianie

    const [filtrText, setFiltrText] = useState("");
    const [allBooks, setAllBooks] = useState(bList);
    const [selectedBooks, setSelectedBooks] = useState(sbList)


    function selectBooks() {
        let locCopyBookList = allBooks.getCopy();
        locCopyBookList.selectBooks();
        setSelectedBooks(locCopyBookList);
    }

    function filtrBooks() {
        
        let locCopyBookList = allBooks.getCopy();

        
        if (filtrText.length > 0) {

                        
            locCopyBookList.filtrBooksfiltrText();
            alert(filtrText);    
            
        }

        setSelectedBooks(locCopyBookList);
    }


    function addNewBook() {

        allBooks.addBook();
        selectedBooks.addBook();

        setAllBooks(allBooks);
        setSelectedBooks(selectedBooks);
    }


    return (
        <Container>
            <Header />

            <Form.Group>
                <Button variant="primary" onClick={selectBooks}>  Pozostaw książki tylko Tolkiena  </Button>
                {' '}
                <Button variant="primary" onClick={addNewBook}>  Dodaj jakąś ksiażkę  </Button>
            </Form.Group>

            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Tytuł</th>
                        <th>Autor</th>
                        <th>Liczba stron</th>
                    </tr>
                </thead>
                <tbody>

                    {selectedBooks.books.map((lbook, index) => (
                        <BookDescriptionHook title={lbook.title} author={lbook.author} pages={lbook.pages} />
                    ))}
                </tbody>
            </Table>

            <Form.Group>
                <Form.Label> Tekst do filtra: <input type="text" name='filtrText' value={filtrText} onChange={(e) => setFiltrText(e.target.value)} /> </Form.Label>
                {' '}
                <Button variant="success" onClick={filtrBooks}>  Filtruj książki  </Button>
            </Form.Group>

            <Footer />
        </Container>
    );
}
