import React, {useState } from 'react'
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';


class TPerson {

    
    constructor(firstname, lastname, age) {
        this.firstname = firstname;
        this.lastname = lastname;
        this.age = age;
    }
}

export default function Table2Hook() {

    const [personList, setPersonList] = useState([]);

    const p1 = new TPerson("Jan", "Kowalski", '35');
    const p2 = new TPerson("Marek", "Nowak", '46');
    const p3 = new TPerson("Anna", "Wazna", '26');
    personList.push(p1); personList.push(p2); personList.push(p3);


    return (

        <Container>

            <Table striped bordered>
                <thead>
                    <tr>
                        <th>ImiÄ™</th> <th>Nazwisko</th> <th>Wiek</th>
                    </tr>
                </thead>
                <tbody>
                    {this.state.personList.map((lperson, index) => (
                        <tr>
                            <td>{lperson.firstname}</td> <td>{lperson.lastname}</td>  <td>{lperson.age}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>

        </Container>
    );
}
