import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.min.css';



export default function LoginWindowHook() {

    const [show, setShow] = useState(false);
    const [username, setUsername] = useState('user');
    const [password, setPassword] = useState('huser');
    const [musername, setMusername] = useState('user');
    const [mpassword, setMpassword] = useState('huser');


    function handleClose(event) {

        let currentButton = event.currentTarget.textContent;

        setShow(false);

        if (currentButton === "OK") {

            setUsername(musername);
            setPassword(mpassword);
        }
        else {
            setMusername(username);
            setMpassword(password);
        }
    }

    function handleCloseHeader() {

        setShow(false);

        setMusername(username);
        setMpassword(password);

    }


    function handleShow() {
        setShow(true);
    }

    function handleChangeUser(e) {
        setMusername(e.target.value);
    }

    function handleChangePassword(e) {
        setMpassword(e.target.value);
    }


    return (
        <Container>

            <h3>Tekst okna logowania: {username}, {password} (HOOK)</h3>

            <Button variant="primary" onClick={handleShow}>
                Otwórz okno logowania
            </Button>

            <Modal show={show} onHide={handleCloseHeader}>
                <Modal.Header closeButton>
                    <Modal.Title>Okno logowania</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="auto">
                            <Form.Label>Podaj nazwę użytkownika:</Form.Label>
                            <Form.Control type="text" value={musername} placeholder="" onChange={handleChangeUser} />
                        </Form.Group>
                        <p></p>
                        <Form.Group className="auto">
                            <Form.Label>Podaj hasło:</Form.Label>
                            <Form.Control type="text" value={mpassword} placeholder="" onChange={handleChangePassword} />
                        </Form.Group>
                    </Form >

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}>
                        OK
                    </Button>
                    <Button variant="secondary" onClick={handleClose}>
                        Anuluj
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
}
