import React from 'react';
import Component from "react";
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Formik, Field, ErrorMessage, FormikHelpers } from "formik";
import * as Yup from "yup";



interface Values {
    name: string;
    age: number;
    gender: string;
}


interface Props {
};


interface State {
    initValues: Values;
    validSchema: Yup.ObjectSchema<any>;

    rname: string;
    rage: number;
    rgender: string;
}


class MyFormikYup extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);

        const locInitialValues = { name: '', age: 0, gender: '' };

        const locValidationSchema = Yup.object({
            name: Yup.string()
                .min(2, "Imię musi posiadać minimum 2 znaki").max(15, "Imię musi posiadać maksimum 15 znaków").required("Pole wymagane"),
            age: Yup.number().typeError("Wiek ma być liczbą całkowitą").min(1, "Wiek musi być większy od 0")
                .max(150, "Wiek musi być nie większy niz 150").required("Wiek jest wymagany"),
            gender: Yup.string().oneOf(["K", "k", "M", "m"], "Niepoprawny kod płci").notRequired()
        });


        this.state = { initValues: locInitialValues, validSchema: locValidationSchema, rname: "", rage: 0, rgender: "" };

    }

    renderError(message: string): string {
        return message;
    }


    render() {

        return (
            <div>
                <h1>Test formika</h1>
                <Formik
                    initialValues={this.state.initValues}

                    validationSchema={this.state.validSchema}

                    onSubmit={(
                        values: Values,
                        { setSubmitting }: FormikHelpers<Values>
                    ) => {

                        this.setState({ rname: values.name });
                        this.setState({ rage: values.age });
                        this.setState({ rgender: values.gender });
                        setSubmitting(false);
                    }}
                >

                    <Form>

                        <h3>Wynik wprowadzania danych: {this.state.rname}, {this.state.rage}, {this.state.rgender}</h3>

                        <div>
                            <label htmlFor="name">Imię</label>
                        </div>
                        <div>
                            <Field id="name" name="name" placeholder="" />
                        </div>
                        <div style={{ color: 'red' }}>
                            <em>
                                <ErrorMessage name="name" render={this.renderError} />
                            </em>
                        </div>

                        <div><p></p></div>

                        <div>
                            <label htmlFor="age">Wiek</label>
                        </div>
                        <Field id="age" name="age" placeholder="" />
                        <div style={{ color: 'red' }}>
                            <em>
                                <ErrorMessage name="age" render={this.renderError} />
                            </em>
                        </div>

                        <div><p></p></div>

                        <div>
                            <label htmlFor="gender">Płeć</label>
                        </div>
                        <div>
                            <Field id="gender" name="gender" placeholder="" />
                        </div>
                        <div style={{ color: 'red' }}>
                            <em>
                                <ErrorMessage name="gender" render={this.renderError} />
                            </em>
                        </div>

                        <div><p></p></div>

                        <button type="submit">Submit</button>

                    </Form>


                </Formik>
            </div >
        );

    }
}


export default MyFormikYup;
