import axios from 'axios';
import React, { useEffect, useState } from 'react';

function App() {
  const [username, setUsername] = useState(123); // Użyj let zamiast const
  const [result, setResult] = useState('');
  const [status, setStatus] = useState('');

  const incrementUsername = () => {
    setUsername(username + 1);
  };

  const getScoreByUsername = async () => {
    // Sprawdź, czy username jest liczbą
    if (isNaN(username)) {
      setStatus('Wprowadzona wartość nie jest liczbą');
      return;
    }

    try {
      const response = await axios.post('http://localhost:8081/api/v1/getlatestscore/');
      let userFound = false;
      let userText = '';

      response.data.forEach((e) => {
        let lusername = e.username;
        let firstPartText = lusername.substring(0, 'ERROR:'.length);

        if (firstPartText !== 'ERROR:' && parseInt(lusername, 10) === username) {
          userText = `${e.id}, ${e.username}, ${e.hiraganaScore1}, ${e.hiraganaScore2}, ${e.hiraganaScore3}, ${e.katakanaScore1}, ${e.katakanaScore2}, ${e.katakanaScore3}\n`;
          setStatus('OK');
          userFound = true;
        } else if (firstPartText === 'ERROR:' && parseInt(lusername, 10) === username) {
          let secondPartText = lusername.substring('ERROR:'.length, lusername.length);
          userText = `${lusername}\n`;
          setStatus(secondPartText);
          userFound = true;
        }
      });

      if (!userFound) {
        setStatus('Użytkownik nie znaleziony');
      }

      setResult(userText);
    } catch (err) {
      let myerror = 'Błąd połączenia sieciowego.' + err;
      setStatus(myerror);
    }
  };

  useEffect(() => {
    // Wywołaj funkcję getScoreByUsername przy ładowaniu strony
    getScoreByUsername();
  }, []); // Pusty tablicowy zależności oznacza, że useEffect zostanie wywołany tylko raz po załadowaniu komponentu

  return (
    <div>
      <div>
        <p>Obecna wartość username: {username}</p>
        <button onClick={incrementUsername}>Zwiększ username o 1</button>
      </div>
      <button onClick={getScoreByUsername}>Pobierz Wynik</button>

      <p>{result}</p>
      <div>Status: {status}</div>
    </div>
  );
}

export default App;
