import React, { Component, FormEvent, ChangeEvent } from "react";

//https://react-bootstrap.github.io/

import { Row, Col, Container } from "react-bootstrap";
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';

import 'bootstrap/dist/css/bootstrap.min.css';

import axios from 'axios';

import BPerson from "./BPerson"
import BPersonList from "./BPersonList"



interface Props {
};

interface State {
    status: string;
    textArea: string;

    newEmail: string;
    newFirstName: string;
    newLastName: string;
    newAge: string;

    emailToRemove: string;

    personList: BPersonList;
}


class BPersonDatabase extends Component<Props, State>
{


    constructor(props: Props) {
        super(props);

        this.state = {
            status: 'OK', textArea: "",
            newEmail: "ola@gmail.com", newFirstName: "Ola", newLastName: "Kama", newAge: "8",
            emailToRemove: "",
            personList: new BPersonList()
        };

        this.state.personList.build();

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.deletePerson = this.deletePerson.bind(this);
    }



    addUser() {
        let email = this.state.newEmail;
        let firstName = this.state.newFirstName;
        let lastName = this.state.newLastName;
        let age = this.state.newAge;

        if ((email.length > 0) && (firstName.length > 0) && (lastName.length > 0) && (age.length)) {

            let p: BPerson = new BPerson(email, firstName, lastName, age);

            this.state.personList.add(p);

            this.setState((prevState) => ({
                personList: prevState.personList
            }));
        }
        else {
            this.setState({ status: "Żadna z danych wstawianej osoby nie może być pusta" });
        }
    }

    deleteUser() {
        let emailToRemove = this.state.emailToRemove;

        if (emailToRemove.length > 0) {

            this.state.personList.deletePerson(emailToRemove);

            this.setState((prevState) => ({
                personList: prevState.personList
            }));
        }
        else {
            this.setState({ status: "Adres email nie może być pusty" });
        }
    }


    handleChange(evt: ChangeEvent<HTMLInputElement>) {

        const name = evt.target.name;

        if (name === "newEmail") {
            this.setState({
                newEmail: evt.target.value
            });
        }
        else
            if (name === "newFirstName") {
                this.setState({
                    newFirstName: evt.target.value
                });
            }
            else
                if (name === "newLastName") {
                    this.setState({
                        newLastName: evt.target.value
                    });
                }
                else
                    if (name === "newAge") {
                        this.setState({
                            newAge: evt.target.value
                        });
                    }
                    else {
                        this.setState({
                            emailToRemove: evt.target.value
                        });
                    }
    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {

        this.addUser();
        event.preventDefault();
    }


    deletePerson() {
        this.deleteUser();
    }


    //'primary', 'secondary',  'success', 'danger', 'warning', 'info', 'light', 'dark',

    render() {
        return (


            <Container>


                <Alert key="primary" variant="primary">
                    This is a primary alert—check it out!
                </Alert>

                <Button as="a" variant="primary">
                    Button as link
                </Button>

                <Alert key="primary" variant="primary">
                    To jest demo linku:
                    <Alert.Link href="https://www.google.com/">Link do www.google.com</Alert.Link>.

                    To jest demo nagłówka:
                    <Alert.Heading>To jest tekst nagłówka</Alert.Heading>

                </Alert>

                <Table striped bordered>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Username</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>1</td>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>@mdo</td>
                        </tr>
                        <tr>
                            <td>2</td>
                            <td>Jacob</td>
                            <td>Thornton</td>
                            <td>@fat</td>
                        </tr>
                        <tr>
                            <td>3</td>
                            <td colSpan={2}>Larry the Bird</td>
                            <td>@twitter</td>
                        </tr>
                    </tbody>
                </Table>



                <form>
                    <Row>
                        <Col xs={12}>
                            <h1>Witamy przy kasie!</h1>
                            <h2>Podaj dane do wysyłki:</h2>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={4}>
                            <p>Imię:</p>
                            <input type="text" name="firstname" />
                        </Col>
                        <Col xs={12} md={4}>
                            <p>Nazwisko:</p>
                            <input type="text" name="lastname" />
                        </Col>
                    </Row>
                </form>




                <h1>Klient dostępu do bazy osób w pamięci RAM</h1>

                <h3>Status: {this.state.status}</h3>

                <Form.Label size="lg"> Status: {this.state.status}</Form.Label>

                <Form.Group className="mb-3">
                    <Form.Label htmlFor="disabledTextInput">Disabled input</Form.Label>
                    
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Email osoby do usunięcia:</Form.Label>
                    <Form.Control type="text" placeholder={this.state.emailToRemove} onChange={this.handleChange} />                    
                </Form.Group>

                <label> Email osoby do usunięcia: <input type="text" name='deleteID' value={this.state.emailToRemove} onChange={this.handleChange} />  </label>

                <Button variant="primary" onClick={this.deletePerson}>
                    Usuń osobę
                </Button>

                <Alert variant="primary">
                    <h4>  <strong>Lista osób</strong> </h4>
                </Alert>


                <Table striped bordered>
                    <thead>
                        <tr>
                            <th>Imię</th>
                            <th>Nazwisko</th>
                            <th>Email</th>
                            <th>Wiek</th>
                        </tr>
                    </thead>
                    <tbody>

                        {this.state.personList.personList.map((lperson, index) => (
                            <tr>
                                <td>{lperson.firstname}</td> <td>{lperson.lastname}</td> <td>{lperson.email}</td>  <td>{lperson.age}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>


            </Container>

        );
    }
}

export default BPersonDatabase;
