import React, { createContext, useContext, useState } from "react";

const LVariablesContext = createContext();

export const LVariablesProvider = ({ children }) => {

    const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [newUserName, setNewUserName] = useState("");
    const [newPassword, setNewPassword] = useState("");

    const contextValue = {

        userNameState: [userName, setUserName],
        passwordState: [password, setPassword],
        newUserNameState: [newUserName, setNewUserName],
        newPasswordState: [newPassword, setNewPassword],

    };

    return (

        <LVariablesContext.Provider value={contextValue}>
        {children}
        </LVariablesContext.Provider>

    );

};

export const useLVariables = () => {

    const context = useContext(LVariablesContext);
    if (!context) {

        throw new Error("useLVariables must be used within a LVariablesProvider");

    }

    return context;
};

export default LVariablesProvider;  // Dodane dla poprawnego eksportu
