import React from 'react';
import { Component, FormEvent, ChangeEvent } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

interface Props {
};

interface State {
  selectedOption: string;
}


class RadiobuttonForm extends Component<Props, State>
{
  constructor(props: Props) {
    super(props);
    this.state = { selectedOption: "Red" };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e: ChangeEvent<HTMLInputElement>): void {
    //alert('Wybrano: ' + e.target.value);
    this.setState({ selectedOption: e.target.value });
  }


  handleSubmit(event: FormEvent<HTMLFormElement>): void {
    alert('Wybrano: ' + this.state.selectedOption);
    event.preventDefault();
  }


  render() {
    return (
      <Form onSubmit={this.handleSubmit}>
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label>Wybierz swój ulubiony kolor:</Form.Label>
          <Form.Check
            type={"radio"}
            label="Red"
            value={"Red"}
            onChange={this.handleChange}
            checked={this.state.selectedOption === "Red"}>
          </Form.Check>
          <Form.Check
            type={"radio"}
            label="Green"
            value={"Green"}
            onChange={this.handleChange}
            checked={this.state.selectedOption === "Green"}>
          </Form.Check>
          <Form.Check
            type={"radio"}
            label="Blue"
            value={"Blue"}
            onChange={this.handleChange}
            checked={this.state.selectedOption === "Blue"}>
          </Form.Check>          
        </Form.Group>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form>
    );
  }


  /*Tak bez react-bootstrap
render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div className="radio">
          <label>
            <input type="radio" value="Red" checked={this.state.selectedOption === "Red"} onChange={this.handleChange} />  Red
          </label>
        </div>

        <div className="radio">
          <label>
            <input type="radio" value="Green" checked={this.state.selectedOption === "Green"} onChange={this.handleChange} />  Blue
          </label>
        </div>

        <div className="radio">
          <label>
            <input type="radio" value="Blue" checked={this.state.selectedOption === "Blue"} onChange={this.handleChange} />  Blue
          </label>
        </div>

        <div>
          Selected option is : {this.state.selectedOption}
        </div>

        <button type="submit">  Submit </button>

      </form>
    );
  }
  */
}

export default RadiobuttonForm;
