import React, {useState } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default function ManyElemFormHook() {

    const [name, setName] = useState("NoName");
    const [age, setAge] = useState("1");
    const [city, setCity] = useState("Rzeszów");


    function handleSubmit(event) {
        alert('Podano: ' + this.state.name + " " + this.state.age + " " + this.state.city);
        event.preventDefault();
    }


    return (
        <Container>

            <h3>Aplikacja demonstrująca okno z formularzem wieloelementowym (HOOK)</h3>

            <Form onSubmit={handleSubmit}>
                <Form.Group className="auto">
                    <Form.Label>Podaj imię:</Form.Label>
                    <Form.Control type="text" name='name' value={name} placeholder="" onChange={(e) => setName(e.target.value)} />
                    <Form.Label>Podaj wiek:</Form.Label>
                    <Form.Control type="text" name='age' value={age} placeholder="" onChange={(e) => setAge(e.target.value)} />
                    <Form.Label>Podaj miasto:</Form.Label>
                    <Form.Control type="text" name='city' value={city} placeholder="" onChange={(e) => setCity(e.target.value)} />
                </Form.Group>
                <p></p>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form >
        </Container>
    );

}
