import React, {useState, Component, FormEvent, ChangeEvent } from "react";

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

 
export default function App() {
  const [counter, setCounter] = useState(0);

  const [username, setUsername] = useState('');


  //increase counter
  const increase = () => {

    let a = parseFloat(username);
    if (isNaN(a)) {

      //this.setState({ status: "Niepoprawny argument: " + this.state.username })
    }
    else {
      this.setState({ counter: this.state.counter + a })
      //this.setState({status: "OK"})                    
    }

    setCounter(count => count + 1);
  };


  /*
  handleChange(e: ChangeEvent<HTMLInputElement>): void {
    //alert('Zmiana: ' + e.target.value);   
    this.setState({ value: e.target.value });
  }


  handleSubmit(event: FormEvent<HTMLFormElement>): void {
    alert('Podano nastepujacy tekst: ' + this.state.value);
    event.preventDefault();
  }*/

  
 
  return (
    <div className="counter">
      <h1>React Counter HOOK INput</h1>
      <span className="counter__output">{counter}</span>
      <div className="btn__container">
        <button className="control__btn" onClick={increase}>+</button>
        
        <input type="text" placeholder="jdoe123" onChange={e => setUsername(e.target.value)} />
      
      <p>{username}</p>

      </div>
    </div>
  );
}
