import React, {useState, Component, FormEvent, ChangeEvent } from "react";


export default function App() {
  const [counter, setCounter] = useState(0);

  const [mynumber, setMyNumber] = useState('');

  const [status, setStatus] = useState('OK');


  //increase counter
  const increase = () => {

    let a = parseFloat(mynumber);
    if (isNaN(a)) {

      setStatus("Niepoprawny argument: " + mynumber );
    }
    else {
      setCounter(counter => counter + a);            
      setStatus("OK" );
    }

  };
 
  return (
    <div className="counter">
      <h1>React Counter HOOK INput</h1>

      <h3>Status: {status}</h3>
      <h3>Stan licznika: {mynumber}</h3>                        

      <span className="counter__output">{counter}</span>
      <div className="btn__container">
        <button className="control__btn" onClick={increase}>Dodaj do sumy</button>
        
        <input type="text" placeholder="1" onChange={e => setMyNumber(e.target.value)} />
      

      </div>
    </div>
  );
}
