import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


import 'bootstrap/dist/css/bootstrap.min.css';


//-------------------------

interface WymianaProps {    
    startkurs : string;
    callback : (dajWynikWymiany : string) => void;        
};

interface WymianaState {  
    kurs : string;   
    kwota : string;        
    
}


class WymianaForm extends Component<WymianaProps, WymianaState>
{
  constructor(props: WymianaProps) {
    super(props);
    
    this.state = { kurs: props.startkurs,  kwota : '0' };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(e: ChangeEvent<HTMLInputElement>): void {
    //alert('Zmiana: ' + e.target.value);   
    
    this.setState({ kwota: e.target.value });
  }


  handleSubmit(event: FormEvent<HTMLFormElement>): void {    
    
    let aktualnyKurs = this.state.kurs;
    
    //console.log("Aktualny kurse:"+aktualnyKurs)
    let wynik : number = Number(aktualnyKurs) * Number(this.state.kwota);

    console.log("Wynik wymiany:"+wynik)
    this.props.callback("Olenka"); 
    
    
    event.preventDefault();
    
    
  }

  render() {
    return (

      <Container>
        
        <h3>Aktualny kurs wymiany: {this.state.kurs} </h3>
        <h3>Aktualna kwota: {this.state.kwota} </h3>
        
        <Form onSubmit={this.handleSubmit}>
          <Form.Group className="auto" onSubmit={this.handleSubmit}>
            <Form.Label>Podaj kwotę do wymiany:</Form.Label>
            <Form.Control type="text" value={this.state.kwota} placeholder="" onChange={this.handleChange} />
          </Form.Group>
          <p></p>
          <Button variant="primary" type="submit">  Wymień  </Button>
        </Form > 

      </Container>

    );
  }
}


//==================================

interface Props {   
    
};

interface State {       
    kurs : string;    
    wymiany : string;
}



class MojKantor extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { kurs: "4.46", wymiany: "",};
     
        
        this.handleChange = this.handleChange.bind(this);        
        this.handleSubmit = this.handleSubmit.bind(this);


    }

    dajWynikWymiany(wynik : string) : void{
                
        this.setState({ wymiany: this.state.wymiany + "," + String(wynik)});               
    }

    handleChange(e: ChangeEvent<HTMLInputElement>): void {
        //alert('Zmiana: ' + e.target.value);   
        this.setState({ kurs: e.target.value });
      }
    
    
      handleSubmit(event: FormEvent<HTMLFormElement>): void {        
        console.log("Nowy kurs="+this.state.kurs);       
        event.preventDefault();
        
      }

      
    render() {
        return (

            <Container>

                <h3>Aktualny kurs kantoru {this.state.kurs} </h3>
                <p></p>

                <WymianaForm startkurs={this.state.kurs} callback={this.dajWynikWymiany} />

                <p></p>

                <Form onSubmit={this.handleSubmit}>
                    <Form.Group className="auto" onSubmit={this.handleSubmit}>
                        <Form.Label>Podaj nowy kurs:</Form.Label>
                        <Form.Control type="text" value={this.state.kurs} placeholder="" onChange={this.handleChange} />
                    </Form.Group>
                    <p></p>
                    <Button variant="primary" type="submit">  Wymień  </Button>
                </Form >

                <p></p>
                <h3>Dotychczasowe wyniki wymiany: {this.state.wymiany} </h3>
                <p></p>

                <p>-------------------------------</p>

            </Container>

        );
    }
}


export default MojKantor;
