import React, { Component } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

//Kilka elementów (CLASS)

class ManyElemForm extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            age: "",
            city: ""
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(evt) {

        const name = evt.target.name;

        if (name === "name") {
            this.setState({ name: evt.target.value });
        }
        else
            if (name === "age") {
                this.setState({ age: evt.target.value });
            }
            else {
                this.setState({ city: evt.target.value });
            }
    }


    handleSubmit(event) {
        alert('Podano: ' + this.state.name + " " + this.state.age + " " + this.state.city);
        event.preventDefault();

    }

    render() {

        return (
            <Container>

                <h3>Aplikacja demonstrująca okno z formularzem wieloelementowym (CLASS)</h3>

                <Form onSubmit={this.handleSubmit}>
                    <Form.Group className="auto">
                        <Form.Label>Podaj imię:</Form.Label>
                        <Form.Control type="text" name='name' value={this.state.name} placeholder="" onChange={this.handleChange} />
                        <Form.Label>Podaj wiek:</Form.Label>
                        <Form.Control type="text" name='age' value={this.state.age} placeholder="" onChange={this.handleChange} />
                        <Form.Label>Podaj miasto:</Form.Label>
                        <Form.Control type="text" name='city' value={this.state.city} placeholder="" onChange={this.handleChange} />
                    </Form.Group>
                    <p></p>
                    <Button variant="primary" type="submit">  Potwierdź  </Button>
                </Form >
            </Container>
        );
    }
}

export default ManyElemForm;
