import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function CounterAppHook() {

  const [myValue, setMyValue] = useState("abc");

  //Obsługa przyciskow


  
    function handleChange(e) {      
      setMyValue(e);                        
    }
  


  const handleSubmit = () => {
    alert('Podano nastepujacy tekst: ' + myValue);
  }

  function handleSubmit1() {
    alert('Podano nastepujacy tekst: ' + myValue);
  }


  return (
    <Container>
      <h3>Aplikacja demonstrująca pole wprowadzanie tekstu jednolinijkowego (HOOK)</h3>

      <Form onSubmit={handleSubmit1}>
        <Form.Group className="auto">
          <Form.Label>Podaj tekst:</Form.Label>
          <Form.Control type="text" value={myValue} placeholder="" onChange={handleChange(e.target.value)} />
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );



