import  SPersonHook from "./SPersonHook"

class SPersonListHook{
    
    constructor(){
        this.personList = [];        
    }

    add(person) {
        this.personList.push(person);
    }

    addPerson(email, firstname, lastname, age) {
        let locPerson = new SPersonHook(email, firstname, lastname, age);
        this.personList.push(locPerson);
    }

   
    get(index)  {
        return this.personList[index];
    }

    size() {
        return this.personList.length;
    }


    //Utworzenie bazy poczatkowej osób
    build() {
        let p1 = new SPersonHook("jkowalski@gmail.com","Jan","Kowalski",35);
        let p2 = new SPersonHook("mnowak@gmail.com","Marek","Nowak",46);
        let p3 = new SPersonHook("awazna@gmail.com","Anna","Wazna",26);      

        this.add(p1);this.add(p2);this.add(p3);
    }

    deletePerson(email) {

        if (this.size() > 0) {

            let locPersonList = [];
            let finded = false;

            for (let i= 0; i < this.size(); i = i + 1) {
                let person = this.get(i);
                let locEmail = person.email;

                if (locEmail != email) {
                    locPersonList.push(person);
                }
                else {
                    finded = true;
                }
            }

            if (!finded) {
                return false;
            }
            else {
                this.personList = locPersonList;
                return true;                
            }
        }
        else{
            return false;            
        }

    }

    copy() {

        let locPersonList = new SPersonListHook();

        if (this.size() > 0) {

            for (let i = 0; i < this.size(); i = i + 1) {
                let person = this.get(i);
                locPersonList.add(person);
            }
        }

        return locPersonList;
    }

    

}

export default SPersonListHook;
