import React, { Component } from "react";

//https://react-bootstrap.github.io/

import Jumbotron from 'react-bootstrap/Jumbotron';
import { Container } from "react-bootstrap";
import Alert from 'react-bootstrap/Alert';

interface Props {
};

interface State {
}


class BootstrapDemo extends Component<Props, State>
{


    constructor(props: Props) {
        super(props);

    }

    //'primary', 'secondary',  'success', 'danger', 'warning', 'info', 'light', 'dark',

    render() {
        return (


            <Container>

                <Jumbotron>
                    <h1>Hello, world!</h1>
                    <p>
                        This is a simple hero unit, a simple jumbotron-style component for calling
                        extra attention to featured content or information.
                    </p>
                    <p>
                        <Button bsStyle="primary">Learn more</Button>
                    </p>
                </Jumbotron>

                <Alert variant="primary">
                    <Alert.Heading>To jest nagłówek kolorowego obszaru typu primary</Alert.Heading>
                    <p>Można jeszcze używać typy: 'secondary',  'success', 'danger', 'warning', 'info', 'light', 'dark'</p>
                    <p>Znacznik p pozwala uzyskać teksty wielolinijkowe</p>
                </Alert>

                <Alert variant="secondary">
                    <h3>To jest demonstracja obszaru typu secondary</h3> <p></p>
                </Alert>

                <Alert variant="success">
                    <h3>To jest demonstracja obszaru typu success</h3> <p></p>
                </Alert>

                <Alert variant="danger">
                    <h3>To jest demonstracja obszaru typu danger</h3> <p></p>
                </Alert>

                <Alert variant="warning">
                    <h3>To jest demonstracja obszaru typu warning</h3> <p></p>
                </Alert>

                <Alert variant="info">
                    <h3>To jest demonstracja obszaru typu info</h3> <p></p>
                </Alert>

                <Alert variant="light">
                    <h3>To jest demonstracja obszaru typu light</h3> <p></p>
                </Alert>

                <Alert variant="dark">
                    <h3>To jest demonstracja obszaru typu dark</h3> <p></p>
                </Alert>



                <Alert variant="primary">
                    <h2><strong>To jest demonstracja użycia linków w komponencie do innych stron:</strong></h2> <p></p>
                    <Alert.Link href="https://www.google.com/">Link do strony www.google.com</Alert.Link> <p></p>
                </Alert>


            </Container>

        );
    }



}

export default BootstrapDemo;
