import React from 'react';
import { Component, ChangeEvent, MouseEvent } from "react";

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import 'bootstrap/dist/css/bootstrap.min.css';

import LoginWindowModal from "./LoginWindowModal";

interface Props {  
};

interface State {
    show: boolean;
    
    username: string;
    password: string;
    
    
}


class LoginWindowButton extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { show: false, username: 'user', password: "huser"};
     
        this.handleShow = this.handleShow.bind(this);

    }

    handleShow(): void {
        this.setState({ show: true });
    }

    showWindow(status : boolean) : void
    {
        if (status)
        {
            this.setState({ show: true });
        }
        else
        {
            this.setState({ show: false });
        }
    }
   
    render() {
        return (
            <div>

                <h3>Tekst okna logowania: {this.state.username}, {this.state.password} </h3>

                <Button variant="primary" onClick={this.handleShow}>
                    Otw√≥rz okno logowania
                </Button>

               
                
            </div>
        );
    }
}


export default LoginWindowButton;
