import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function DNameFormHook() {


  const [state, setState] = useState({ valueFirstName: "Jan", valueLastName: "Kowal" });  

  const [myValue, setMyValue] = useState("abc");

  
  //Obsługa przycisku
  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano nastepujace teksty: ' + state.valueFirstName+ " "+ state.valueLastName);
  }

  //Obsluga aktualizacji stanu
  const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === "firstName") {
      this.setState({ valueFirstName: evt.target.value });
    }
    else {
      this.setState({ valueLastName: evt.target.value });
    }
  };


  return (

    <Container>

        <h3>Aplikacja demonstrująca dwa pola wprowadzania tekstu jednolinijkowego (HOOK)</h3>

        <Form onSubmit={this.handleSubmit}>
            <Form.Group className="auto" onSubmit={this.handleSubmit}>
                <Form.Label>Podaj imię:</Form.Label>
                <Form.Control type="text" name='firstName' value={this.state.valueFirstName} placeholder="" onChange={this.handleChange} />
            </Form.Group>
            <Form.Group className="auto" onSubmit={this.handleSubmit}>
                <Form.Label>Podaj nazwisko:</Form.Label>
                <Form.Control type="text" name='lastName' value={this.state.valueLastName} placeholder="" onChange={this.handleChange} />
            </Form.Group>
            <p></p>
            <Button variant="primary" type="submit">  Potwierdź  </Button>
        </Form >
    </Container>
);

}
