import React, { Component } from "react";
import Container from 'react-bootstrap/Container';
import Table from 'react-bootstrap/Table'; 
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import 'bootstrap/dist/css/bootstrap.min.css';

import Footer from "./footer";
import Header from "./header";
import PersonDescription from "./PersonDescription";

import SPerson from "./SPerson"
import SPersonList from "./SPersonList"
import ReadPerson from "./ReadPerson"
import DeletePerson from "./DeletePerson"

//Baza danych osób w pamięci RAM oparta na 4 głównych komponentach klasowych (PDatabaseAp, PersonDescription, ReadPerson, DeletePerson)
//Korzysta także z 2 kompotentów pomocniczych:  Header i Footer
//Pokazuje obsługę bazy danych w aplikacji typu SPA


interface Props {
};

interface State {
    personList: SPersonList;
    status: string;
    isError: boolean;
};


class PDatabaseApp extends Component<Props, State> {

    constructor(props: Props) {
        super(props);

        this.state = {
            personList: new SPersonList(), status: "Start", isError: false
        }

        this.state.personList.build();


        this.addNewPerson = this.addNewPerson.bind(this);
        this.deletePerson = this.deletePerson.bind(this);

    }

    addNewPerson(p: SPerson): void {

        this.state.personList.add(p);

        this.setState((prevState) => ({
            personList: prevState.personList,
            status: "Dodano osobę: " + p.firstname + " " + p.lastname,
            isError: false
        }));
    }

    deletePerson(email: string): void {

        let result: boolean = this.state.personList.deletePerson(email);
        if (!result) {
            this.setState((prevState) => ({                
                status: "Brak osoby o adresie email: " + email,
                isError: true
            }));
        }
        else {
            this.setState((prevState) => ({
                personList: prevState.personList,
                status: "Usunieto osobę o adresie email: " + email,
                isError: false
            }));
        }
    }

    render() {
        return (
            <Container>
                <Header />

                {!this.state.isError && <Alert variant="success"> <strong>Status: {this.state.status}</strong> </Alert> }   
                {this.state.isError && <Alert variant="danger"> <strong>Status: {this.state.status}</strong> </Alert> }   

                
                <Table striped bordered>
                    <thead>
                        <tr>
                            <th>Email</th>
                            <th>Imię</th>
                            <th>Nazwisko</th>
                            <th>Wiek</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.personList.personList.map((lperson, index) => (
                            <PersonDescription email={lperson.email} firstname={lperson.firstname} lastname={lperson.lastname} age={lperson.age} />
                        ))}
                    </tbody>
                </Table>

                <ReadPerson callback={this.addNewPerson} />

                <DeletePerson callback={this.deletePerson} />

                <Footer />
            </Container>
        );
    }
}
export default PDatabaseApp;
