import React, { Component } from "react";
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';


//Tablica na podstawie listy rekordów (CLASS) 

class TPerson {

    
    constructor(firstname, lastname, age) {
        this.firstname = firstname;
        this.lastname = lastname;
        this.age = age;
    }
}


class Table2 extends Component
{
    constructor(props) {
        super(props);

        this.state = {
            personList: []
        };

        let p1 = new TPerson("Jan", "Kowalski", '35');
        let p2 = new TPerson("Marek", "Nowak", '46');
        let p3 = new TPerson("Anna", "Wazna", '26');

        this.state.personList.push(p1); this.state.personList.push(p2); this.state.personList.push(p3);

    }

    render() {

        return (

            <Container>

                <h3>Aplikacja demonstrująca tabelę o zmiennej zawartości (CLASS)</h3>

                <Table striped bordered>
                    <thead>
                        <tr>
                            <th>Imię</th> <th>Nazwisko</th> <th>Wiek</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.personList.map((lperson, index) => (
                            <tr>
                                <td>{lperson.firstname}</td> <td>{lperson.lastname}</td>  <td>{lperson.age}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>

            </Container>
        );
    }
}

export default Table2;
