import React, { Component } from "react";

//Aplikacja demonstrująca obsługę trzech przycisków


class CounterApp extends Component {
    constructor(props) {
        super(props);
        
        this.state = {
            counter: 0
        }
       
        //Zapenienie reakcji na kliknięcie na przyciski
        this.increase = this.increase.bind(this);
        this.decrease = this.decrease.bind(this);
        this.clear = this.clear.bind(this);
    }

    //Obsługa przyciskow
    increase() {
        this.setState((prevState) => ({
            counter: prevState.counter + 1
        }));
    }
    
    decrease() {
        this.setState((prevState) => ({
            counter: prevState.counter - 1
        }));
    }
    
    clear() {
        this.setState({
            counter: 0
        });
    }
    
    render() {
        return (
            <div>
                <h2>Aplikacja demonstrująca obsługę przycisków (JS) </h2>

                <h3>Stan licznika: {this.state.counter}</h3>                    
                <button onClick={this.increase}>Zwiększ o jeden</button>
                <br /><br />
                <button onClick={this.decrease}>Zmniejsz o jeden</button>
                <br /><br />
                <button onClick={this.clear}>Zeruj</button>
            </div>
        );
    }
}
export default CounterApp;
