import React, {useState} from "react";


export default function App() {

  const [counter, setCounter] = useState(0);
  const [mynumber, setMyNumber] = useState('2');
  const [status, setStatus] = useState('OK');


  //increase counter
  const increase = () => {

    let a = parseFloat(mynumber);
    if (isNaN(a)) {

      setStatus("Niepoprawny argument: " + mynumber );
    }
    else {
      setCounter(counter => counter + a);            
      setStatus("OK" );
    }

  };

  return (
    <div className="counter">
      <h3>Status: {status}</h3>
      <h3>Stan licznika: {counter}</h3>
      <div className="btn__container">
        <button onClick={increase}>Dodaj do sumy</button>
        <br /><br />
        <input type="text" value={mynumber} onChange={e => setMyNumber(e.target.value)} />
      </div>

    </div>
  );
}
