import React, { Component, FormEvent, ChangeEvent } from "react";

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


interface Props {
};

interface State {
  value: string;
}


class TextAreaForm extends Component<Props, State>
{
  constructor(props: Props) {
    super(props);
    this.state = { value: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e: ChangeEvent<HTMLTextAreaElement>): void {
    this.setState({ value: e.target.value });
  }


  handleSubmit(event: FormEvent<HTMLFormElement>) :void {
    alert('Podano nastepujacy tekst: ' + this.state.value);
    event.preventDefault();
  }


  render() {
    return (

      <Container>
        
        <h3>Aplikacja demonstrująca pole wprowadzania tekstu wielolinijkowego</h3>
        
        <Form onSubmit={this.handleSubmit}>
          <Form.Group className="auto" onSubmit={this.handleSubmit}>
            <Form.Label>Podaj tekst:</Form.Label>
            <Form.Control as="textarea" type="text" value={this.state.value} placeholder="" onChange={this.handleChange} spellCheck="false" />
          </Form.Group>
          <p></p>
          <Button variant="primary" type="submit">  Potwierdź  </Button>
        </Form >      
        </Container> 

    );
  }
}

export default TextAreaForm;
