import  BPerson from "./BPerson"

class BPersonList{

    public personList : BPerson[];
    
    constructor(){
        this.personList = [];        
    }

    public add(person : BPerson) : void {
        this.personList.push(person);
    }

    public addPerson(email : string, firstname: string, lastname : string, age : string) : void {
        let locPerson = new BPerson(email, firstname, lastname, age);
        this.personList.push(locPerson);
    }

   
    public get(index : number) : BPerson {
        return this.personList[index];
    }

    public size() : number {
        return this.personList.length;
    }


    //Utworzenie bazy poczatkowej ksiazek
    public build() : void {
        let p1 = new BPerson("jkowalski@gmail.com","Jan","Kowalski",'35');
        let p2 = new BPerson("mnowak@gmail.com","Marek","Nowak",'46');
        let p3 = new BPerson("awazna@gmail.com","Anna","Wazna",'26');
        

        this.add(p1);this.add(p2);this.add(p3);
    }


    public deletePerson(email : string) : void {

        if (this.size()>0)
        {

            let locPersonList : BPerson[] = [];  

            for (let i : number=0; i<this.size(); i=i+1)
            {
                let person = this.get(i);
                let locEmail = person.email;
                                
                if (locEmail!=email){
                    locPersonList.push(person);        
                }
            }            

            this.personList = locPersonList;

        }
        
    }

}

export default BPersonList;
