import React from 'react';
import { Component, ChangeEvent, MouseEvent } from "react";

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import 'bootstrap/dist/css/bootstrap.min.css';

interface Props {
};

interface State {
    show: boolean;
    value: string;
    mvalue: string;
}


class TwoWindows extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { show: false, value: '', mvalue: 'abc' };

        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);

        this.handleChange = this.handleChange.bind(this);
        this.handleCloseHeader = this.handleCloseHeader.bind(this);
    }


    handleClose(event: MouseEvent<HTMLButtonElement>): void {

        let currentButton = event.currentTarget.textContent;

        this.setState({ show: false });

        if (currentButton === "OK") {
            this.setState({ value: this.state.mvalue });
        }
        else {
            this.setState({ mvalue: this.state.value });
        }
    }

    handleCloseHeader(): void {

        this.setState({ show: false });
        this.setState({ mvalue: this.state.value });
    }


    handleShow(): void {
        this.setState({ show: true });
    }

    handleChange(e: ChangeEvent<HTMLInputElement>): void {
        this.setState({ mvalue: e.target.value });
    }


    render() {
        return (
            <div>

                <h3>Tekst okna modalnego: {this.state.value}</h3>

                <Button variant="primary" onClick={this.handleShow}>
                    Otw√≥rz okno modalne
                </Button>

                <Modal show={this.state.show} onHide={this.handleCloseHeader}>
                    <Modal.Header closeButton>
                        <Modal.Title>Okno modalne</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <Form.Group className="auto">
                                <Form.Label>Podaj tekst:</Form.Label>
                                <Form.Control type="text" value={this.state.mvalue} placeholder="" onChange={this.handleChange} />
                            </Form.Group>
                        </Form >

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" onClick={this.handleClose}>
                            OK
                        </Button>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Anuluj
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}


export default TwoWindows;
